<?php	include "../funciones/conexion.php";	include "../funciones/funciones.php";	session_start();	include "funciones/configuracion.php";			/*	Obtiene la versión del sistema */	$sql = "SELECT version FROM mon_changelog ORDER BY id_changelog DESC LIMIT 1";	$resul = mysqli_query($conexion, $sql);	if(!$resul){		$error = mysqli_error($conexion);		include "error.php";		exit();	}	$version = array();	while($fila = mysqli_fetch_array($resul)){		$version = $fila['version'];	}		/* Existe una sesión activa */	if(isset($_SESSION['administrador']) && $_SESSION['administrador'] >= 1){			/* Mantiene al usuario online y actualiza sus datos de */		/* última conexion. */		$sql = "UPDATE usuarios			SET online=1,				ip_ultima_conexion='".$_SERVER['REMOTE_ADDR']."',				fecha_ultima_conexion = now()			WHERE email='".$_SESSION['email']."'";		$resul = mysqli_query($conexion, $sql);		if(!$resul){			$error = mysqli_error($conexion);			include "error.php";			exit();		}				/* Archivo donde están todas las reglas de la sección "administración". */		include "admin.php";		exit();	}		/* En cualquier otro caso, se le redirige a la página principal. */	header('Location: /');	exit();?>