<?php	include "includes/cabecera_admin.php";?>	<script>		function confirmar_borrar_usuario(param){			$("#mensajes").html("<div id='tarjeta'><h1>Confirmación</h1><p>¿Seguro que deseas borrar al usuario "+param+"?</p></div>");			$("#tarjeta").append("<div id='botonera'><form action='' method='POST'><input type='submit' class='cancelar' name='borrar_usuario' value='<?php echo $idioma['adm_borrar']; ?>' /><input type='button' class='aceptar' value='Cancelar' onclick='javascript:cancelar_borrar_usuario();'/><input type='hidden' id='borrar_usuario_id' name='borrar_usuario_id' value='"+param+"' /></form></div>");			$("#mensajes").fadeIn("fast");		}				function cancelar_borrar_usuario(param){			$("#mensajes").fadeOut("fast");		}	</script>	<h1>		<img height="40px" style="vertical-align: middle;" src="../img/homescreen.png" /> 		<?php echo $idioma['glo_usuarios']." (".$usuarios_registrados." ".$idioma['glo_usuarios_registrados'].")"; ?>	</h1>	<hr />		<table style="width: 100%;">		<tr>			<th>ID</th>			<th><?php echo $idioma['glo_email']; ?></th>			<th><?php echo $idioma['glo_nombre']; ?></th>			<th><?php echo $idioma['glo_tipo_cuenta']; ?></th>			<th><?php echo $idioma['cfg_ultima_conexion']; ?></th>			<th><?php echo $idioma['cfg_ultima_ip']; ?></th>			<th><?php echo $idioma['glo_estado']; ?></th>		</tr>	<?php	foreach($usuarios as $usuario){		echo '<tr>';			echo '<td>'.$usuario["id_usuario"].'</td>';			echo '<td>'.$usuario["email"].'</td>';			echo '<td>'.$usuario["nombre"].'</td>';			echo '<td><img height="18px" class="explicacion" style="vertical-align: middle;" src="http://www.imperdiblesoft.com/img/tipos_cuenta/'.$tipo_cuenta[$usuario["tipo_cuenta"]]['nombre'].'.png" alt="'.$idioma["adm_cuenta"].' '.$tipo_cuenta[$usuario["tipo_cuenta"]]["nombre"].'" /></td>';			echo '<td>'.formatear_fecha($usuario["fecha_ultima_conexion"]).'</td>';			echo '<td>'.$usuario["ip_ultima_conexion"].'</td>';						/*	Establece los botones de estado */			if($usuario['online'] == 1 && $usuario['ausente'] == 1){				$usuario['estado'] = "online";				$nombre_input = "desconectar_usuario";				$titulo_input = $idioma['adm_desconectar_usuario'];							}else if($usuario['online'] == 0 && $usuario['ausente'] == 1){				$usuario['estado'] = "online-aus";				$nombre_input = "desconectar_usuario";				$titulo_input = $idioma['adm_desconectar_usuario'];							}else{				$usuario['estado'] = "offline";				$nombre_input = "conectar_usuario";				$titulo_input = $idioma['adm_conectar_usuario'];			}			echo '<td>';				echo '<form class="personal" action="admin-'.$_GET["seccion"].'" method="POST">';					echo '<input type="submit" class="conectar explicacion" style="background: url(../img/'.$usuario["estado"].'.png) no-repeat" name="'.$nombre_input.'" value="" alt="'.$titulo_input.'" />';					echo '<input type="hidden" name="conectar_usuario_id" value="'.$usuario["id_usuario"].'" />';				echo '</form>';			echo '</td>';						/*	Establece los botones del edición o eliminación */			if($_SESSION['administrador'] >= 1){				echo '<td>';					echo "<form class='personal' action='admin-".$_GET['seccion']."' method='POST'>";						echo "<input type='submit' class='editar' name='editar_usuario' title='".$idioma['adm_editar']."' value='' />";												if($_SESSION['administrador'] >= 3){							echo "<input type='button' class='borrar' title='".$idioma['adm_borrar']."' value='' onclick='javascript:confirmar_borrar_usuario(\"".$usuario["id_usuario"]."\");'/>";						}						echo "<input type='hidden' name='modificar_usuario_id' value='".$usuario["id_usuario"]."' />";					echo "</form>";				echo '</td>';			}		echo '</tr>';	}	echo "</table>";	include "includes/pie.php"; ?>